// Group team members by team number
const teamGroups = {};

for (const item of $input.all()) {
  const teamNum = item.json['Team Number'];
  const firstName = item.json['First Name'];
  const lastName = item.json['Last Name'];
  const email = item.json['Email'];
  const country = item.json['Country'];
  const availability = item.json['Availabilty']; // Note: keeping your spelling
  const linkedin = item.json['LinkedIn URL'];
  const discord = item.json['Discord Username'];

  if (!teamGroups[teamNum]) {
    teamGroups[teamNum] = [];
  }

  teamGroups[teamNum].push({
    firstName: firstName,
    lastName: lastName,
    fullName: `${firstName} ${lastName}`,
    email: email,
    country: country,
    availability: availability,
    linkedin: linkedin,
    discord: discord,
    teamNumber: teamNum
  });
}

// Create output for each team member with their teammates
const output = [];
for (const teamNum in teamGroups) {
  const teammates = teamGroups[teamNum]; // This defines teammates

  // Now use teammates.forEach - this is where your code goes
  teammates.forEach(member => {
    const otherTeammates = teammates.filter(t => t.email !== member.email);

    // Format teammates as a simple string
    const teammatesList = otherTeammates.map(tm => 
      `${tm.fullName} (${tm.email}) - ${tm.country} - LinkedIn: ${tm.linkedin} - Available: ${tm.availability}`
    ).join('\n');

    output.push({
      json: {
        recipientName: member.fullName,
        recipientFirstName: member.firstName,
        recipientLastName: member.lastName,
        recipientEmail: member.email,
        recipientCountry: member.country,
        recipientAvailability: member.availability,
        recipientLinkedIn: member.linkedin,
        teamNumber: teamNum,
        teammatesList: teammatesList
      }
    });
  });
}

return output;

